mapper.select:
SELECT * FROM classes

     WHERE name = #{name}

 order by name

SELECT * FROM teacher_information


          login_name = #{loginName}


          AND id = #{id}

SELECT * FROM teacher_information
  WHERE login_name IN(
SELECT login_name FROM role WHERE role_type = #{roleType} AND role_status = 0

    AND login_name = #{loginName}

)

mapper.update:
UPDATE teacher_information
number = #{number}, name = #{name}, sex = #{sex}, id_cart = #{idCart}, positional_titles = #{positionalTitles}, nation = #{nation}, place = #{place}, address = #{address}, birthday = #{birthday}, phone = #{phone}, department = #{department}, classes = #{classes}, subject = #{subject},
WHERE login_name = #{loginName}

UPDATE student_information
number = #{number}, name = #{name}, sex = #{sex}, id_cart = #{idCart}, nation = #{nation}, place = #{place}, address = #{address}, birthday = #{birthday}, phone = #{phone}, classes = #{classes}, enrolment_time = #{enrolmentTime},
WHERE login_name = #{loginName}

mapper.select:
SELECT * FROM student_information
WHERE login_name IN(
SELECT login_name FROM role WHERE role_type = #{roleType} AND role_status = 0

    AND login_name = #{loginName}

)
